import React from "react"
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";

const BasicTable = () =>{
  const [getUsers, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const Getusers = async () => {
      try {
        const response = await axios.get("http://localhost:4000/users/getUsers");
        console.log(response);
        setIsLoading(false)
        if (response.data) {
          setUsers(response.data.users);

        } else {
          console.error('API response is empty or falsy.');
        }
      } catch (error) {
        console.error('Error fetching user data:', error.message);
      }
    }

    Getusers();
  }, [isLoading])

  return (
    <div className="table">

    <div className="container-scroller ttb" >


              <div className="col-lg-12 grid-margin stretch-card">
                <div className="card">
                  <div className="card-body">
                    <h4 className="card-title">Users</h4>
                    <p className="card-description"> All Users
                    </p>
                    <table className="table table-bordered">
                      <thead>
                        <tr>
                        <th> Sr.No. </th>
                          <th> Name </th>
                          <th>Email </th>
                          <th> Joining Date </th>
                        </tr>
                      </thead>
                      <tbody>
                      { getUsers.map((user, index) => (
                          <tr key={index+1}>
                            <td>{index+1}</td>
                            <td>{user.username}</td> {/* Replace with the actual property from your user object */}
                            <td>{user.email}</td>
                            <td>{user.signupDate}</td>
                             <td><button>
                             Update</button>
                              <button >Delete</button></td>
                          </tr>
                        ))}
                      </tbody>

                    </table>
                  </div>
                </div>
              </div>

    </div>

    </div>
  )
}
export default BasicTable;
